var mongoose = require("mongoose");
var categorySchema = new mongoose.Schema({
  name: {
    type: String,
    required: true,
    unique: true,
    trim: true
  },
  description: {
    type: String,
    required: false
  },
  image: {
    type: String,
    required: false
  },
