    "enum": ["active", "inactive"],
    "default": "active"
  },
  parentCategory: {
    type: mongoose.Schema.Types.ObjectId,
    ref: "Category",
    "default": null
  },
  createdBy: {
    type: mongoose.Schema.Types.ObjectId,
    ref: "User",
    required: true
  }
}, {
  timestamps: true
});
categorySchema.index({
  status: 1
});
module.exports = mongoose.model("Category", categorySchema);
