"use strict";

// src/models/product.js
var mongoose = require("mongoose");
var productSchema = new mongoose.Schema({
  name: {
    type: String,
    required: true,
    trim: true
  },
  description: {
    type: String,
    required: true
  },
  price: {
    type: Number,
    required: true,
    min: 0
  },
  category: {
    type: mongoose.Schema.Types.ObjectId,
    ref: "Category",
    required: true
  },
  images: [{
    type: String,
    required: true
  }],
  stock: {
    type: Number,
    required: true,
    min: 0,
    "default": 0
  },
  status: {
    type: String,
    "enum": ["active", "inactive"],
    "default": "active"
  },
  createdBy: {
    type: mongoose.Schema.Types.ObjectId,
    ref: "User",
    required: false,
    "default": null
  }
}, {
  timestamps: true
});

// Index để tăng tốc query
productSchema.index({
  category: 1,
  status: 1
});
productSchema.index({
  createdAt: -1
});
productSchema.index({
  name: "text",
  description: "text"
});
module.exports = mongoose.model("Product", productSchema);
//# sourceMappingURL=data:application/json;charset=utf-8;base64,

